parameters.functionCode:
// Validate and prepare new event data
const input = items[0].json;
const payload = input.body || input;

// Required fields
const required = ['name', 'date'];
const missing = required.filter(f => !payload[f]);
if (missing.length > 0) {
  throw new Error(`Missing required field(s): ${missing.join(', ')}`);
}

// Validate calendarDetails.location
if (!payload.calendarDetails || !payload.calendarDetails.location) {
  throw new Error('Missing required field: calendarDetails.location');
}

// Generate ID from name and date (for deduplication)
const eventName = payload.name.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '');
const eventDate = payload.date.substring(0, 10).replace(/-/g, '');
const eventId = `${eventName}-${eventDate}`;

// Check if AI should generate description
const needsAI = !payload.description || payload.description.trim() === '' || payload.useAI === true;

// Build clean event object
const newEvent = {
  id: eventId,
  name: payload.name.trim(),
  date: payload.date,
  link: payload.link || '',
  image: payload.image || '',
  description: payload.description || '',
  calendarDetails: {
    location: payload.calendarDetails.location,
    description: payload.calendarDetails.description || '',
    durationHours: payload.calendarDetails.durationHours || 4
  },
  showMoreInfo: payload.showMoreInfo !== undefined ? payload.showMoreInfo : true,
  showBookNow: payload.showBookNow !== undefined ? payload.showBookNow : false,
  showRemindMe: payload.showRemindMe !== undefined ? payload.showRemindMe : true
};

items[0].json = { newEvent, needsAI };
return items;

