// Add AI-generated description to the event
const aiResponse = items[0].json;
const newEvent = $('Validate Event').first().json.newEvent;

// Get the AI-generated text
let aiDescription = '';
if (aiResponse.message && aiResponse.message.content) {
  aiDescription = aiResponse.message.content.trim();
} else if (aiResponse.text) {
  aiDescription = aiResponse.text.trim();
} else if (typeof aiResponse === 'string') {
  aiDescription = aiResponse.trim();
}

// Update the event with AI description
newEvent.description = aiDescription || newEvent.description;

// Also generate calendar description if empty
if (!newEvent.calendarDetails.description) {
  newEvent.calendarDetails.description = newEvent.description.replace(/^[^\w]+/, '').substring(0, 100);
}

items[0].json = { newEvent };
return items;

// Pass through without AI - keep original event
const newEvent = $('Validate Event').first().json.newEvent;
items[0].json = { newEvent };
return items;

// Merge new event into existing events array
const gh = items[0].json;
const fileSha = gh.sha; // Required for updating the file

// Decode existing events
let events = [];
if (gh.content) {
  const decoded = Buffer.from(gh.content, 'base64').toString('utf8');
  try {
    events = JSON.parse(decoded);
    if (!Array.isArray(events)) events = [];
  } catch (e) {
    events = [];
  }
}

// Get the new event from the merge node (could be from AI or Skip path)
const newEvent = $('Merge Paths').first().json.newEvent;

// Check if event already exists (by ID)
const existingIndex = events.findIndex(e => e.id === newEvent.id);
if (existingIndex >= 0) {
  // Update existing event
  events[existingIndex] = newEvent;
} else {
  // Add new event
  events.push(newEvent);
}

// Sort events by date
events.sort((a, b) => new Date(a.date) - new Date(b.date));

items[0].json = {
  updatedEvents: JSON.stringify(events, null, 2),
  sha: fileSha,
  eventName: newEvent.name,
  eventId: newEvent.id,
  isUpdate: existingIndex >= 0,
  usedAI: newEvent.description && newEvent.description.length > 0
};

return items;
